checksum = 104;
for (t=0; t<kod.length; t++)
{
    value = kod.charCodeAt(t) -32;
    checksum = checksum + (value * (t+1));
}
checksum = checksum % 103;
checksumChar = checksum + 32;
if (checksumChar > 126)
